mapper.select:
select

from tb_post_comment where is_deleted = 0

  and post_id = #{postId}

order by comment_id desc

  limit #{start},#{limit}

select count(*)
from tb_post_comment where is_deleted = 0

  and post_id = #{postId}

mapper.update:
update tb_post_comment
set is_deleted = 1
where comment_id = #{commentId,jdbcType=BIGINT}
